import * as React from 'react';
import { useEffect } from 'react';
import { CssBaseline, Stack } from '@mui/material';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import './css/style.css'
import Heading from './Heading';
import Grid from '@mui/material/Unstable_Grid2'; // Grid version 2
import TextField1 from './TextField1';
import TextField2 from './TextField2';
import ExchangeButton from './ExchangeButton';
import TextField3 from './TextField3';
import TextField4 from './TextField4';
import DateInput from './DateInput';
import { LocalizationProvider } from '@mui/x-date-pickers-pro';
import { AdapterDayjs } from '@mui/x-date-pickers-pro/AdapterDayjs'
import QuestionSVG from './QuestionSVG';
import MultiSelect from './MultiSelect';
import PrintIcon from './PrintIcon';
import ButtonCur01 from './ButtonCur01';
import ButtonCur02 from './ButtonCur02';

function OnlineTradingButton() {
  return (
    <Button variant="contained" className='button1' color='primary'>
      Online trading
    </Button>
  )
}

// Get current date
function getCurrentDate() {
  const currentDate = new Date();

  // Lấy thông tin năm, tháng, ngày
  const year = currentDate.getFullYear();
  const month = String(currentDate.getMonth() + 1).padStart(2, '0'); // Tháng được đếm từ 0 đến 11
  const day = String(currentDate.getDate()).padStart(2, '0');

  // Trả về chuỗi năm-tháng-ngày
  return `${year}-${month}-${day}`;
}


export default function App() {

  // left side currency
  const [cur01, setCur01] = React.useState('USD');
  // left side value
  const [data01, setData01] = React.useState('1');
  // right side currency
  const [cur02, setCur02] = React.useState('VND');
  // right side value
  const [data02, setData02] = React.useState(24000);

  //Date
  const [date, setDate] = React.useState(getCurrentDate())

  // Handling event for currency of Left side
  const handlingClick01 = (value) => {
    setCur01(value);
    console.log('rendering HandlingClick Left Currency, current Left currency:' + cur01)
  }

  // Handling event for currency of Right side

  const handlingClick02 = (value) => {
    setCur02(value);
    console.log('rendering HandlingClick Right Currency, current Right currency:' + cur02)
  }

  const handlingLeftTextField = (value) => {
    console.log('rendering handlingLeftTextField, with current value: ' + data01);
    setData01(value);
  }

  const handlingRightTextField = (value) => {
    console.log('rendering handlingRightTextField, with current value: ' + data02);
    setData02(value);
  }

  const handlingExchangeButton = (firstData, secondData) => {
    console.log('rendering handlingExchangeButton with values: ' + cur01 + ' ' + cur02);
    setCur01(secondData);
    setCur02(firstData);
  }

  const handlingDateInput = (value) => {
    console.log('rendering handlingDateInput, preDate: ' + date);
    setDate(value);
  }
  const [count, setCount] = useState(0);

  useEffect(() => {
    // Đoạn mã được thực thi mỗi khi count thay đổi
    console.log('Count changed:', count);
  }, [date]);


  return (

    <Container maxWidth='fixed' className='mainContainer'>
      <CssBaseline />
      <Grid container spacing={1}>
        {/* Header */}
        <Grid xs={7}>
          <Heading moneyAmount={data01} currency1={cur01} currency2={cur02} />
        </Grid>
        {/* Online trading button */}
        <Grid xs={5}>
          <OnlineTradingButton />
        </Grid>
        {/* Form  */}
        <Grid xs={12}>
          {/* Contain all the Transaction interfaces */}
          <div className='box'>
            {/* Contain 4 textFields and money currency options */}
            <Grid container spacing={3}>
              {/*  Left side */}
              <Grid xs={5.5} display="flex" justifyContent="" flexDirection={'column'} gap={'16px'} >
                <TextField1 data={cur01} />
                <TextField3 onChange={handlingLeftTextField} data={data01} />
                <ButtonCur01 onClick={handlingClick01} />
              </Grid>
              <Grid xs={1} display="flex" justifyContent="center" alignItems='center'>
                <ExchangeButton onClick={handlingExchangeButton} firstData={cur01} secondData={cur02} />
              </Grid>
              {/* Right side */}
              <Grid xs={5.5} display="flex" justifyContent="center" flexDirection={'column'} gap={'16px'} >
                <TextField2 data={cur02} />
                <TextField4 firstCur={cur01} secondCur={cur02} currentMoney={data01} result={data02} currDate={date} setResult={handlingRightTextField} />
                <ButtonCur02 onClick={handlingClick02}></ButtonCur02>
              </Grid>
            </Grid>
            {/* Cointains Date and Preview interBank rate */}
            <Stack direction='row' spacing={0} justifyContent="center" >
              <div className='secondContainer'>
                <p>Date</p>
                <DateInput date={date} onChange={handlingDateInput} />
              </div>
              <div className='thirdContainer'>
                <QuestionSVG />
                <MultiSelect />
                <p>Preview interbank rate</p>
              </div>
            </Stack>
            {/* Contains Printer */}
            <Stack direction='row' spacing={0} justifyContent="space-between" className='PrintSection'>
              <a href='#' className='dataLink'>Hide Advanced Currency Data</a>
              <PrintIcon />
            </Stack>

            {/* Description at the end of the Box */}
            <p id='description'>OANDA's currency calculator tools use OANDA Rates™, the touchstone FX rates compiled from leading market data contributors.</p>
          </div>
        </Grid>
      </Grid>
    </Container>
  );
}
