import * as React from 'react';
import TextField from '@mui/material/TextField';
import { Box } from '@mui/material';
import Autocomplete from '@mui/material/Autocomplete';
import '../Styles/style.css'

// Currencies table
const currencyMappings = {
    SGD: "Singapore Dollar",
    MYR: "Malaysian Ringgit",
    EUR: "Euro",
    USD: "US Dollar",
    AUD: "Australian Dollar",
    JPY: "Japanese Yen",
    CNH: "Chinese Yuan",
    HKD: "Hong Kong Dollar",
    CAD: "Canadian Dollar",
    INR: "Indian Rupee",
    DKK: "Danish Krone",
    GBP: "British Pound",
    RUB: "Russian Ruble",
    NZD: "New Zealand Dollar",
    MXN: "Mexican Peso",
    IDR: "Indonesian Rupiah",
    TWD: "Taiwan Dollar",
    THB: "Thai Baht",
    VND: "Vietnamese Dong"
};

//Input:abbreviation ; output: Full name of the currency
function convertAbbreviationToCurrency(abbreviation) {
    const currency = currencyMappings[abbreviation];
    return currency ? currency : null; // Trả về null nếu không tìm thấy đơn vị tiền tệ
}

//Input: Full name of the currency; output: abbreviation
function convertCurrencyToAbbreviation(currency) {
    for (const [abbreviation, value] of Object.entries(currencyMappings)) {
        if (value.toLowerCase() === currency.toLowerCase()) {
            return abbreviation;
        }
    }
    return null; // Trả về null nếu không tìm thấy viết tắt cho đơn vị tiền tệ
}


//Get country by their currency
function getCountryByCurrency(currency) {
    return countries.find(country => country.currency === currency);
}

export default function FromCurrencyTextField({ onChange }) {

    // return (
    //     <TextField
    //         id='textField1' variant="outlined" fullWidth value={convertCurrencyAbbreviations(props.data)}
    //         autoComplete='ABC'
    //     />
    // )

    console.log(convertCurrencyToAbbreviation('Vietnamese Dong'));

    return (
        <Autocomplete
            className="country-select-demo"
            value={getCountryByCurrency('US Dollar')}
            sx={{ width: 300 }}
            options={countries}
            autoHighlight
            getOptionLabel={(option) => option.currency}
            // onChange={(event, country) => {
            //     onChange(newValue);
            // }}
            renderOption={(props, option, state) => (
                <Box
                    component="li"
                    key={option.code} // use the code property as the key value
                    sx={{ '& > img': { mr: 2, flexShrink: 0 } }}
                    {...props}
                >
                    <img
                        loading="lazy"
                        width="20"
                        src={`https://flagcdn.com/w20/${option.code.toLowerCase()}.png`}
                        srcSet={`https://flagcdn.com/w40/${option.code.toLowerCase()}.png 2x`}
                        alt=""
                    />
                    {option.currency} ({option.country})
                </Box>
            )}
            renderInput={(params) => (
                <TextField
                    {...params}
                    inputProps={{
                        ...params.inputProps,
                        autoComplete: 'new-password', // disable autocomplete and autofill
                    }}
                />
            )}
        />
    );

}

