import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Stack } from '@mui/material';
import Button from '@mui/material/Button';
import './css/style.css'
import UseFetchExchange2 from './Fetch/useFetchExchange2';
import { useEffect } from 'react';
import axios from 'axios';

// Calculating Exchanges
function convertCurrency(moneyAmount, fromCurrency, toCurrency, rates) {
    if (fromCurrency === toCurrency) {
        return moneyAmount;
    }

    if (!(fromCurrency in rates) || !(toCurrency in rates)) {
        return null;
    }

    var fromRate = rates[fromCurrency];
    var toRate = rates[toCurrency];

    var convertedAmount = moneyAmount * (toRate / fromRate);
    return convertedAmount;
}




export default function TextField4({ firstCur, secondCur, currentMoney }) {

    // Fetching API
    // const data = UseFetchExchange2('https://openexchangerates.org/api/latest.json?app_id=01341f5261444649a612793df73c7130');

    const [data, setData] = React.useState(null);
    const [result, setResult] = React.useState(1);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('https://openexchangerates.org/api/latest.json?app_id=01341f5261444649a612793df73c7130');
                setData(response.data);
            } catch (error) {
                console.error(error);
            }
        };
        fetchData();
    }, []);


    console.log('tessst3----------');
    console.log(data);
    console.log(result);

    useEffect(
        () => {
            if (data) {
                console.log('--------222222--------');
                console.log(firstCur);
                console.log(secondCur);
                console.log(currentMoney);
                // console.log(data.rates)

                const rates = data.rates;
                console.log('tessst2---------')
                console.log(data.rates);
                const fromCurrency = firstCur;
                const toCurrency = secondCur;
                const moneyAmount = currentMoney;

                const result2 = Math.round(convertCurrency(moneyAmount, fromCurrency, toCurrency, rates));
                setResult(result2);
                console.log(result2);
            }
        }, [firstCur, currentMoney]
    );



    return (
        <>
            {data ? (
                <TextField
                    id="textField34"
                    label=""
                    variant="filled"
                    color="primary"
                    value={result}
                    fullWidth
                />
            ) : (
                <TextField
                    id="textField34"
                    label=""
                    variant="filled"
                    color="primary"
                    value={1}
                    fullWidth
                />
            )}

        </>
    )
}
