import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Stack } from '@mui/material';
import Button from '@mui/material/Button';
import './css/style.css'
import UseFetchExchange2 from './Fetch/useFetchExchange2';

// Calculating Exchanges
function exchangeCurrency(amount, fromCurrency, toCurrency, exchangeRates) {
    if (!exchangeRates.hasOwnProperty(fromCurrency) || !exchangeRates.hasOwnProperty(toCurrency)) {
        throw new Error('Invalid currency');
    }

    const usdAmount = amount / exchangeRates[fromCurrency];
    const convertedAmount = usdAmount * exchangeRates[toCurrency];

    return convertedAmount;
}




export default function TextField4({ firstCur, secondCur, currentMoney }) {

    // Fetching API
    const data = UseFetchExchange2('https://openexchangerates.org/api/latest.json?app_id=01341f5261444649a612793df73c7130');
    console.log('--------222222--------');
    console.log(data);

    //Retrieve the Rates data from
    const myRate = data.rates;



    const exchangedMoney = exchangeCurrency(parseInt(currentMoney), firstCur, secondCur, myRate);


    return (
        <div>
            <TextField id="textField34" label="" variant="filled" color='primary' value={exchangedMoney} fullWidth />
        </div>
    )
}
