import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import './css/style.css'
import { Stack } from '@mui/material';
import Button from '@mui/material/Button';
import UseFetchCurrencies from './Fetch/useFetchCurrencies';



export default function TextField3(props) {

    // Lay API currencies
    const [data] = UseFetchCurrencies();

    // truyen state qua props
    const [cur, curOnChange] = props;

    const handlingClick = (newCur) => {
        curOnChange(newCur)
    }

    return (
        <div>
            <TextField id="textField34" label="" variant="filled" color='primary' fullWidth />
            <Stack direction='row' alignItems="center"
                className='CurrencyRow' >
                {data && data.map(
                    (currency, index) => {
                        return (
                            <Button variant="text" className='button' key={'cur1' + index}
                                onClick={handlingClick(currency)}>{currency}</Button>
                        )
                    }
                )}
            </Stack>
        </div>
    )
}
