import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import './css/style.css'
import { Stack } from '@mui/material';
import Button from '@mui/material/Button';
import UseFetchCurrencies from './Fetch/useFetchCurrencies';

export default function ButtonCur01({ onClick }) {

    // Lay API currencies
    const [data] = UseFetchCurrencies();

    // Handling Click: Set click value to Global State (App)

    const handlingClickButton = (event) => {
        var value = event.target.value;
        onClick(value);
    }

    return (
        <>
            <Stack direction='row' alignItems="center"
                className='CurrencyRow' >
                {data && data.map(
                    (currency, index) => {
                        return (
                            <Button variant="text" className='button' key={'cur1' + index} value={currency}
                            >{currency}</Button>
                        )
                    }
                )}
            </Stack>
        </>
    )
}
