import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Stack } from '@mui/material';
import Button from '@mui/material/Button';
import './css/style.css'
import UseFetch from './Fetch/useFetchCurrencies';
import UseFetchExchange from './Fetch/useFetchExchange';

export default function TextField4({ firstCur, secondCur, currentMoney }) {

    // RemoveDots
    function removeDotsAndConvertToNumber(str) {
        const regex = /\./g;
        const numberString = str.replace(regex, '');
        const number = Number(numberString);
        return number;
    }

    //Format number with dots
    function formatNumberWithDots(number) {
        const str = number.toString();
        const regex = /\B(?=(\d{3})+(?!\d))/g;
        return str.replace(regex, '.');
    }



    Total = () => {
        const cur01 = firstCur;
        console.log('cur01: ' + cur01);
        const cur02 = secondCur;
        console.log('cur02: ' + cur02);
        // Fetch Exchange data 
        const moneyAmount = parseInt(UseFetchExchange(firstCur, secondCur));
        // Exchanging
        console.log('Calculating - rendering TextField4');
        const money = parseInt(removeDotsAndConvertToNumber(currentMoney));
        console.log('total: ' + money);
        return formatNumberWithDots(money * moneyAmount);
    }

    const total = Total();


    return (
        <div>
            <TextField id="textField34" label="" variant="filled" color='primary' value={total} fullWidth />
        </div>
    )
}
