import * as React from 'react';
import TextField from '@mui/material/TextField';
import '../Styles/style.css'
//Return name of the currencies
function convertCurrencyAbbreviations(input) {
    const currencyMappings = {
        SGD: "Singapore Dollar",
        MYR: "Malaysian Ringgit",
        EUR: "Euro",
        USD: "US Dollar",
        AUD: "Australian Dollar",
        JPY: "Japanese Yen",
        CNH: "Chinese Yuan",
        HKD: "Hong Kong Dollar",
        CAD: "Canadian Dollar",
        INR: "Indian Rupee",
        DKK: "Danish Krone",
        GBP: "British Pound",
        RUB: "Russian Ruble",
        NZD: "New Zealand Dollar",
        MXN: "Mexican Peso",
        IDR: "Indonesian Rupiah",
        TWD: "Taiwan Dollar",
        THB: "Thai Baht",
        VND: "Vietnamese Dong"
    };

    const abbreviations = Object.keys(currencyMappings);
    const regex = new RegExp(abbreviations.join("|"), "gi");

    return input.replace(regex, match => currencyMappings[match.toUpperCase()] || match);
}

export default function FromCurrencyTextField(props) {

    return (
        <TextField
            id='textField1' variant="outlined" fullWidth value={convertCurrencyAbbreviations(props.data)}
            autoComplete='ABC'
        />
    )
}
