import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Stack } from '@mui/material';
import Button from '@mui/material/Button';
import './css/style.css'
import UseFetchExchange2 from './Fetch/useFetchExchange2';

// Calculating Exchanges
function exchangeCurrency(amount, fromCurrency, toCurrency, exchangeRates) {
    if (!exchangeRates.hasOwnProperty(fromCurrency) || !exchangeRates.hasOwnProperty(toCurrency)) {
        throw new Error('Invalid currency');
    }

    const usdAmount = amount / exchangeRates[fromCurrency];
    const convertedAmount = usdAmount * exchangeRates[toCurrency];

    return convertedAmount;
}


// RemoveDots
function removeDotsAndConvertToNumber(str) {
    const regex = /\./g;
    const numberString = str.replace(regex, '');
    const number = Number(numberString);
    return number;
}

//Format number with dots
function formatNumberWithDots(number) {
    const str = number.toString();
    const regex = /\B(?=(\d{3})+(?!\d))/g;
    return str.replace(regex, '.');
}


