import * as React from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Stack } from '@mui/material';
import Button from '@mui/material/Button';
import './css/style.css'
import UseFetchExchange2 from './Fetch/useFetchExchange2';
import { useEffect } from 'react';

// Calculating Exchanges
function convertCurrency(moneyAmount, fromCurrency, toCurrency, rates) {
    if (fromCurrency === toCurrency) {
        return moneyAmount;
    }

    if (!(fromCurrency in rates) || !(toCurrency in rates)) {
        return null;
    }

    var fromRate = rates[fromCurrency];
    var toRate = rates[toCurrency];

    var convertedAmount = moneyAmount * (toRate / fromRate);
    return convertedAmount;
}




export default function TextField4({ firstCur, secondCur, currentMoney }) {

    // Fetching API
    const [data] = UseFetchExchange2('https://openexchangerates.org/api/latest.json?app_id=01341f5261444649a612793df73c7130');

    useEffect(
        () => {
            console.log('--------222222--------');
            console.log(data);
            console.log(firstCur);
            console.log(secondCur);
            console.log(currentMoney);
            // console.log(data.rates)

            const rates = data.rates;
            const fromCurrency = firstCur;
            const toCurrency = secondCur;
            const moneyAmount = currentMoney;

            const result = Math.round(convertCurrency(moneyAmount, fromCurrency, toCurrency, rates));
            console.log(result);
        }, [firstCur, currentMoney]
    );



    return (
        <>
            {data ? (
                <TextField
                    id="textField34"
                    label=""
                    variant="filled"
                    color="primary"
                    value={result}
                    fullWidth
                />
            ) : (
                <TextField
                    id="textField34"
                    label=""
                    variant="filled"
                    color="primary"
                    value={1}
                    fullWidth
                />
            )}

        </>
    )
}
